create table book (id bigint not null auto_increment, creation_date_time datetime, last_change_timestamp datetime, author varchar(255), description varchar(255), isbn varchar(255), no_of_pages bigint not null, title varchar(255) not null, type varchar(255) not null, primary key (id));
create table course (id bigint not null auto_increment, creation_date_time datetime, last_change_timestamp datetime, default_time_in_weeks integer not null, description varchar(255), title varchar(255), type varchar(255), primary key (id));
create table course_books (id bigint not null auto_increment, creation_date_time datetime, last_change_timestamp datetime, book_id bigint, course_id bigint, primary key (id));
create table image (id bigint not null auto_increment, creation_date_time datetime, last_change_timestamp datetime, book_id bigint, course_id bigint, image longblob, user_id bigint, primary key (id));
create table proficiency (id bigint not null auto_increment, creation_date_time datetime, last_change_timestamp datetime, value varchar(255), primary key (id));
create table user (id bigint not null auto_increment, creation_date_time datetime, last_change_timestamp datetime, auth_token varchar(255), email varchar(255) not null, email_verify_token varchar(255), password varchar(255) not null, verified bit not null, primary key (id));
create table user_courses (id bigint not null auto_increment, creation_date_time datetime, last_change_timestamp datetime, completion_rate integer not null, course_id bigint, current_status varchar(255), end_date tinyblob, proficiency varchar(255), start_date tinyblob, user_id bigint, weekly_hours_id bigint, weekly_pages_id bigint, primary key (id));
create table weekly_hours (id bigint not null auto_increment, creation_date_time datetime, last_change_timestamp datetime, friday integer not null, monday integer not null, saturday integer not null, sunday integer not null, thursday integer not null, tuesday integer not null, wednesday integer not null, primary key (id));
create table weekly_pages (id bigint not null auto_increment, creation_date_time datetime, last_change_timestamp datetime, friday integer not null, monday integer not null, saturday integer not null, sunday integer not null, thursday integer not null, tuesday integer not null, wednesday integer not null, primary key (id));
alter table user_courses add constraint FK_or6ijxxwjil7gxbs5esif4k3l foreign key (weekly_hours_id) references weekly_hours (id);
alter table user_courses add constraint FK_fo70lvqgfxehgyme6m7hwhm7b foreign key (weekly_pages_id) references weekly_pages (id);